/**
 * @param {string} s
 * @param {string} t
 * @return {string}
 */
var minWindow = function(s, t) {
  let left = 0
  let right = 0
  let window = {}
  let res = "", len = Infinity
  const need = arr2Map(t.split(''))
  
  const sArr = s.split('')
  while (right < s.length) {
    right++
    window = arr2Map(sArr.slice(left, right))
    while(match(window, need)) {
      if (!res || ((right - left) < res.length)) {
        res = s.slice(left, right)
      }
      left++
      window = arr2Map(sArr.slice(left, right))
    }
  }
  return res
};


function arr2Map(arr) {
  return arr.reduce((acc, cur) => {
    acc[cur] = acc[cur] ? (acc[cur] + 1) : 1
    return acc
  }, {})
}

// 这个地方的时间复杂度太高了
function match(source, target) {
  return Object.keys(target).every(key => source[key] >= target[key])
}

