/**
 * @param {string} s
 * @param {string} t
 * @return {string}
 */
var minWindow = function (s, t) {
  let map = {}
  let resLen = s.length + 1
  let start = 0
  let missingType = 0
  for (const c of t) {
    if (!map[c]) {
      map[c] = 1
      missingType++
    } else {
      map[c]++ 
    }
  }

  let l = 0, r = 0
  for (; r < s.length; r++) {
    const rightChar = s[r]
    if (map[rightChar] !== undefined) map[rightChar]--
    if (map[rightChar] == 0) missingType--
    while(missingType <= 0) {
      const leftChar = s[l]
      // 这里的代码顺序不对，导致结果错误, 具体看 good.js
      l++
      map[leftChar]++
      if (map[leftChar] > 0) missingType++
      if (r - l + 1 < resLen) {
        resLen = r - l + 1
        start = l
      }
    }
  }
  return s.substring(start, start + resLen)
}



// 输入: s = "a", t = "aa"
// 输出: ""

// 输入：s = "ADOBECODEBANC", t = "ABC"
// 输出："BANC"

// console.log(minWindow(s1, t1))
// console.log(minWindow(s2, t2))
console.time()
console.log(
  minWindow(
    '',
    ''
  )
)
console.timeEnd()
